import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder; public class Test { public static void main(String[] args) { BCryptPasswordEncoder encoder = new BCryptPasswordEncoder(); String rawPassword = "123456"; String encodedPassword = "$2a$10$N.zmdr9k7uOCQb376NoUnuTJ8iKXIGfR5sF2PzinGoHsqt/jWK2K6"; System.out.println("Raw password: " + rawPassword); System.out.println("Encoded password: " + encodedPassword); System.out.println("Matches: " + encoder.matches(rawPassword, encodedPassword)); } }
